package day04;

import java.util.Scanner;

public class NestedForEx01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/*	구구단 2~ 9단 출력하는 코드 작성
 * 	
 */
		
		int num =3 ;
		for	(num=2; num<=9;num++) {
			System.out.println(num+" 단 ");
			for(int i = 1; i<= 9; i++) {
				System.out.println(num+"x"+i + "="+ num*i);
					
				
		}
			}
			
		}
		
		
		
		
			
			
		
		
		
	}


