package day04;

public class VeriableEx05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//		실수 소수점 유효범위 확인하는 예제 
		float num1 = 0.12345678f;
		double num2 = 0.1234567812345678;

		System.out.printf("%.8f\n", num1);
		System.out.printf("%.20f\n", num2);

		/*
		 * hello world 5번 출력하는 예제
		 * 
		 * while (조건식) { 실행문; }
		 * 
		 * 초기화; //조건식에 사용할 변수 while(조건식) { 실행문; 증감식; ;
		 * 
		 *  	반복횟수: i는 1부터 5까지 1씩 증가 => 초기화,증감식,조건식 
		 *  	규칙성: hello world 를 콘솔에 출력 => 실행문
		 *  	반복문 종료 후 : 없음 	
		 */
		int i = 1;
		while (i < 5) {
			System.out.println("hello world");
			++i; // i++; // I+=1 // i = i+1;

			
			
			
			
			
			
			
			
			
			
			
			
			
			
			
		}

	}
}




